import{o,b as r,ar as t}from"./index.7836d928.js";const p={class:"markdown-body"},c=t(`<p>\u6240\u8C13Promise\uFF0C\u7B80\u5355\u8BF4\u5C31\u662F\u4E00\u4E2A\u5BB9\u5668\uFF0C\u91CC\u9762\u4FDD\u5B58\u7740\u67D0\u4E2A\u672A\u6765\u624D\u4F1A\u7ED3\u675F\u7684\u4E8B\u4EF6\uFF08\u901A\u5E38\u662F\u4E00\u4E2A\u5F02\u6B65\u64CD\u4F5C\uFF09\u7684\u7ED3\u679C\u3002\u4ECE\u8BED\u6CD5\u4E0A\u8BF4\uFF0CPromise \u662F\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u4ECE\u5B83\u53EF\u4EE5\u83B7\u53D6\u5F02\u6B65\u64CD\u4F5C\u7684\u6D88\u606F\u3002Promise \u63D0\u4F9B\u7EDF\u4E00\u7684 API\uFF0C\u5404\u79CD\u5F02\u6B65\u64CD\u4F5C\u90FD\u53EF\u4EE5\u7528\u540C\u6837\u7684\u65B9\u6CD5\u8FDB\u884C\u5904\u7406\u3002</p><h2>\u7B80\u5355\u89E3\u8BFB</h2><p>Promise\u4ECE\u5B57\u9762\u610F\u4E49\u5C31\u662F\u627F\u8BFA\u3002\u800C\u627F\u8BFA\u7684\u7279\u70B9\u662F\uFF0C\u4E00\u65E6\u5B9A\u4E0B\u5C31\u4E0D\u53EF\u4EE5\u6539\u53D8\u627F\u8BFA\u7684\u5185\u5BB9\uFF0C\u4E5F\u65E0\u6CD5\u53D6\u6D88\u3002\u6240\u4EE5Promise\u5177\u6709\u4EE5\u4E0B\u51E0\u4E2A\u7279\u70B9</p><pre><code>1. \u5BF9\u8C61\u4E0D\u4F1A\u53D7\u5230\u5916\u754C\u7684\u5F71\u54CD\uFF0C\u4E00\u8BFA\u5343\u91D1\u4E0D\u53EF\u6539\u53D8\u3002\u800CPromise\u6709\u4E09\u79CD\u72B6\u6001\uFF0Cpending\uFF08\u8FDB\u884C\u4E2D\uFF09\u3001fulfilled\uFF08\u5DF2\u6210\u529F\uFF09\u548Crejected\uFF08\u5DF2\u5931\u8D25\uFF09\u3002\u8FD9\u4E5F\u540C\u65F6\u5BF9\u5E94\u4E86Promise\u5E38\u7528\u7684\u4E09\u4E2A\u51FD\u6570\uFF0Cnew Promise()\uFF08\u8FDB\u884C\u4E2D\uFF09\u3001resolv()\uFF08\u5DF2\u6210\u529F\uFF09\u548Creject()\uFF08\u5DF2\u5931\u8D25\uFF09\u3002

2. \u627F\u8BFA\u90FD\u662F\u94FE\u5F0F\u7684\uFF0C\u6BD4\u5982\uFF1A\u6211\u627F\u8BFA(Promise)\u6211\u738B\u5883\u6CFD\u7EDD\u5BF9\u4E0D\u5403\u4F60\u4E00\u53E3\u996D\uFF01,\u6210\u529F\u4E86(fulfilled)\u6211\u5C31\u662F\u94C1\u9AA8\u94EE\u94EE\uFF0C\u5426\u5219(rejected)\u6211\u5C31\u6B7B\u5916\u8FB9\u4ECE\u8FD9\u513F\u8DF3\u4E0B\u53BB\u3002
</code></pre><h2>\u57FA\u672C\u7528\u6CD5</h2><pre><code>var promise = new Promise(function(resolve,reject){
    if(wjz.eat()){
        // \u5931\u8D25
        reject(&quot;\u771F\u9999\uFF01&quot;);
    }else{
        // \u6210\u529F
        resolve(&quot;\u94C1\u9AA8\u94EE\u94EE\u738B\u5883\u6CFD\uFF01&quot;);
    }
});
promise.then(function(json) {
    return json;
}).then(function(post) {
    // ...&quot;\u94C1\u9AA8\u94EE\u94EE\u738B\u5883\u6CFD\uFF01&quot;
    console.log(post);
}).catch(function(err){
    // ...&quot;\u771F\u9999\uFF01&quot;
    console.log(err);
});
</code></pre><p>\u8FD9\u4E00\u6BB5\u4F2A\u4EE3\u7801\u8868\u660E\u4E86\u4EE5\u4E0B\u51E0\u70B9</p><ol><li><p>Promise\u5BF9\u8C61\u53EF\u4EE5\u94FE\u5F0F\u8C03\u7528\u3002</p></li><li><p>\u6BCF\u4E00\u4E2Athen\u51FD\u6570\u90FD\u4F1A\u8FD4\u56DE\u4E00\u4E2A\u65B0\u7684Promise\u5BF9\u8C61\uFF0C\u5E76\u4EE5\u8FD4\u56DE\u503C\u4F5C\u4E3A\u4E0B\u4E00\u4E2Athen\u4E2D\u7684\u51FD\u6570\u7684\u53C2\u6570\u3002</p></li><li><p>\u5982\u679C\u4E0D\u662F\u8C03\u7528reject\u65B9\u6CD5\uFF0C\u800C\u662F\u76F4\u63A5\u629B\u9519\u3002\u90A3\u4E48catch\u5F53\u4E2D\u7684err\u4F1A\u662F\u9519\u8BEF\u7C7B\u578B\u672C\u8EAB\uFF0C\u800C\u8C03\u7528reject\u65B9\u6CD5\u5219err\u4F1A\u662F\u5176\u8C03\u7528\u7684\u53C2\u6570\u3002</p></li></ol><blockquote><p>\u503C\u5F97\u6CE8\u610F\u7684\u4E00\u70B9\u662F\uFF0C\u5F53\u4F60\u8C03\u7528\u4E86reject()\u6216\u8005resolve()\u6765\u5224\u5B9A\u8FD9\u4E00\u4E2A\u627F\u8BFA\u662F\u6210\u529F\u4E86\u8FD8\u662F\u5931\u8D25\u4E86\u4E4B\u540E\uFF0C\u5C31\u610F\u5473\u7740\u8FD9\u4E00\u4E2A\u627F\u8BFA\u7684\u72B6\u6001\u5DF2\u5B9A\u627F\u8BFA\u7ED3\u675F\u3002\u4E4B\u540E\u5373\u4F7F\u5728<code>resolve(&quot;\u94C1\u9AA8\u94EE\u94EE\u738B\u5883\u6CFD\uFF01&quot;)</code>\u4E4B\u540E\u629B\u9519\u4E5F\u5BF9\u5916\u754C\u4E0D\u4F1A\u6709\u4EFB\u4F55\u5F71\u54CD,\u4F46\u662F\u4E4B\u540E\u7684\u4EE3\u7801\u4F1A\u88AB\u6267\u884C\u3002</p></blockquote><h2>Promise.prototype.finally()</h2><pre><code>finally\u65B9\u6CD5\u7528\u4E8E\u6307\u5B9A\u4E0D\u7BA1 Promise \u5BF9\u8C61\u6700\u540E\u72B6\u6001\u5982\u4F55\uFF0C\u90FD\u4F1A\u6267\u884C\u7684\u64CD\u4F5C\u3002\u8BE5\u65B9\u6CD5\u662F ES2018 \u5F15\u5165\u6807\u51C6\u7684\u3002
</code></pre><p>\u7B80\u5355\u6765\u8BF4 \u5C31\u548Ctry\u3001catch\u3001finally\u662F\u5DEE\u4E0D\u591A\u7684\u3002</p><pre><code>promise
.then(result =&gt; {\xB7\xB7\xB7})
.catch(error =&gt; {\xB7\xB7\xB7})
.finally(() =&gt; {\xB7\xB7\xB7});
</code></pre><blockquote><p>\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u5BF9\u4E8Efinally\u6765\u8BF4\uFF0C\u5E76\u4E0D\u4F1A\u5728\u610F\u8FD9\u4E00\u6B21\u627F\u8BFA\u662F\u5426\u6267\u884C\u6210\u529F\u4E86\uFF0C\u8FD8\u662F\u6267\u884C\u5931\u8D25\u4E86\u3002\u4E5F\u56E0\u6B64\u5B83\u7684\u56DE\u8C03\u51FD\u6570\u4E0D\u63A5\u53D7\u4EFB\u4F55\u53C2\u6570</p></blockquote><p>\u5176\u5B9E\u65E0\u8BBA\u662Fcatch\u8FD8\u662Ffinally\uFF0C\u672C\u8D28\u4E0A\u90FD\u662F\u7279\u6B8A\u5B9E\u73B0\u7684then\u3002</p><h2>Promise.all()</h2><blockquote><p>Promise.all\u65B9\u6CD5\u7528\u4E8E\u5C06\u591A\u4E2A Promise \u5B9E\u4F8B\uFF0C\u5305\u88C5\u6210\u4E00\u4E2A\u65B0\u7684 Promise \u5B9E\u4F8B\u3002</p></blockquote><p>all\u65B9\u6CD5\u63A5\u53D7\u4E00\u4E2APromise\u5BF9\u8C61\u7EC4\u6210\u7684\u6570\u7EC4\uFF08Promise.all\u65B9\u6CD5\u7684\u53C2\u6570\u53EF\u4EE5\u4E0D\u662F\u6570\u7EC4\uFF0C\u4F46\u5FC5\u987B\u5177\u6709 Iterator \u63A5\u53E3\uFF0C\u4E14\u8FD4\u56DE\u7684\u6BCF\u4E2A\u6210\u5458\u90FD\u662F Promise \u5B9E\u4F8B\u3002\uFF09\u3002</p><pre><code>const p = Promise.all([p1, p2, p3]);
</code></pre><p>p\u7684\u72B6\u6001\u7531p1\u3001p2\u3001p3\u51B3\u5B9A\uFF0C\u5206\u6210\u4E24\u79CD\u60C5\u51B5\u3002</p><p>\uFF081\uFF09\u53EA\u6709p1\u3001p2\u3001p3\u7684\u72B6\u6001\u90FD\u53D8\u6210fulfilled\uFF0Cp\u7684\u72B6\u6001\u624D\u4F1A\u53D8\u6210fulfilled\uFF0C\u6B64\u65F6p1\u3001p2\u3001p3\u7684\u8FD4\u56DE\u503C\u7EC4\u6210\u4E00\u4E2A\u6570\u7EC4\uFF0C\u4F20\u9012\u7ED9p\u7684\u56DE\u8C03\u51FD\u6570\u3002</p><p>\uFF082\uFF09\u53EA\u8981p1\u3001p2\u3001p3\u4E4B\u4E2D\u6709\u4E00\u4E2A\u88ABrejected\uFF0Cp\u7684\u72B6\u6001\u5C31\u53D8\u6210rejected\uFF0C\u6B64\u65F6\u7B2C\u4E00\u4E2A\u88ABreject\u7684\u5B9E\u4F8B\u7684\u8FD4\u56DE\u503C\uFF0C\u4F1A\u4F20\u9012\u7ED9p\u7684\u56DE\u8C03\u51FD\u6570\u3002</p><blockquote><p>\u7B80\u5355\u6765\u8BF4\u5C31\u662Fall\u65B9\u6CD5\u4F1A\u628A\u591A\u4E2APromise\u5BF9\u8C61\u5408\u5E76\u4E3A\u4E00\u4E2A\u3002\u5F53\u6240\u6709Promise\u5B8C\u6210\u7684\u65F6\u5019\u5C31\u4F1A\u89E6\u53D1p\u96C6\u5408\u5BF9\u8C61\u7684then\u7684\u56DE\u8C03\uFF0C\u800C\u5176\u4E2D\u4EFB\u610F\u4E00\u4E2A\u65B9\u6CD5\u5931\u8D25\u5219\u4F1A\u8C03\u7528p\u7684catch\u56DE\u8C03\u65B9\u6CD5\u3002</p></blockquote><p>\u4F5C\u7528\u5B9E\u4F8B\u5728\u4E8E\u5F02\u6B65\u8C03\u7528\u591A\u4E2AAPI\u83B7\u5F97\u5168\u90E8\u83B7\u5F97\u7ED3\u679C\u540E\u518D\u8FDB\u884C\u5176\u4ED6\u64CD\u4F5C\u3002</p><blockquote><p>\u6CE8\u610F\uFF0C\u5982\u679C\u4F5C\u4E3A\u53C2\u6570\u7684 Promise \u5B9E\u4F8B\uFF0C\u81EA\u5DF1\u5B9A\u4E49\u4E86catch\u65B9\u6CD5\uFF0C\u90A3\u4E48\u5B83\u4E00\u65E6\u88ABrejected\uFF0C\u5E76\u4E0D\u4F1A\u89E6\u53D1Promise.all()\u7684catch\u65B9\u6CD5\u3002</p></blockquote><h2>Promise.race()</h2><p>race\uFF0C\u5B57\u9762\u4E0A\u7684\u610F\u601D\u662F\u7ADE\u8D5B\u3002</p><p>Promise.race()\u65B9\u6CD5\u540C\u6837\u662F\u5BF9\u5BF9\u4E2APromise\u5BF9\u8C61\u5408\u5E76\u7684\u64CD\u4F5C\u3002</p><p>\u7EFC\u5408\u4E0A\u9762\u4E24\u70B9\u6765\u770B\u5F88\u660E\u663Erace\u548Call\u76F8\u53CD\uFF0Crace\u7684\u4F5C\u7528\u5C31\u5728\u4E8E\u5F53Promise\u96C6\u5408\u5F53\u4E2D\u7684\u5176\u4E2D\u4E00\u4E2APromise\u6267\u884C\u5B8C\u6210\u7684\u65F6\u5019\uFF0C\u96C6\u5408\u5C31\u53D8\u6210\u4E86\u5DF2\u5B8C\u6210\u72B6\u6001\u4E86\u3002\u800C\u96C6\u5408\u5F53\u4E2D\u6240\u6709\u7684Promise\u53D8\u6210rejected\u4E4B\u540E\u624D\u4F1A\u4F7F\u96C6\u5408\u53D8\u6210rejected\u72B6\u6001\u3002</p><h2>Promise.resolve()</h2><p>\u5C06\u73B0\u6709\u5BF9\u8C61\u8F6C\u6362\u6210\u4E00\u4E2APromise\u5BF9\u8C61\u3002</p><blockquote><p>\u4E0A\u65B9\u7684all\u548Crace\u65B9\u6CD5\uFF0C\u5982\u679C\u6570\u7EC4\u5F53\u4E2D\u7684\u5BF9\u8C61\u4E0D\u662Fpromise\u5BF9\u8C61\u7684\u8BDD\u4F1A\u5148\u8C03\u7528resolve\u65B9\u6CD5\u8F6C\u6362\u6210Promise\u5BF9\u8C61\u3002</p></blockquote><h2>Promise.try()</h2><p>\u8FD9\u4E2A\u65B9\u6CD5\u4E3B\u8981\u662F\u4E3A\u4E86\u89E3\u51B3\uFF0C\u5F53\u4F60\u4E0D\u60F3\u5F02\u6B65\uFF0C\u4F46\u662F\u53C8\u60F3\u5229\u7528Promise\u7684\u6D41\u5F0F\u89E3\u51B3\u65B9\u6CD5\u3002</p><p>\u4F60\u53EF\u80FD\u4F1A\u60F3\u53EF\u4EE5\u76F4\u63A5\u4F7F\u7528Promise\u89E3\u51B3\u95EE\u9898\uFF0C\u4F8B\u5982\uFF1A</p><pre><code>    Promise.resolve().then(f)
</code></pre><p>\u4F46\u662F\u8FD9\u6837\u53EF\u80FD\u4F1A\u5BFC\u81F4\u4E00\u4E2A\u95EE\u9898\uFF0C\u5982\u679Cf\u662F\u540C\u6B65\u51FD\u6570\uFF0C\u90A3\u4E48\u5B83\u4F1A\u5728\u672C\u8F6E\u4E8B\u4EF6\u5FAA\u73AF\u7684\u672B\u5C3E\u6267\u884C\u3002\u8FD9\u6837\u4F60\u5C31\u4E22\u5931\u4E86\u4ED6\u7684\u540C\u6B65\u7279\u6027\u3002</p><p>\u6240\u4EE5\u4F60\u53EF\u4EE5\u91C7\u7528try\u6765\u89E3\u51B3\uFF1A</p><pre><code>Promise.try(f).then((result)=&gt;{
    console.log(result);
}).catch((err)=&gt;{
    console.log(err);
})
</code></pre><blockquote><p>\u4E8B\u5B9E\u4E0A\uFF0CPromise.try\u5C31\u662F\u6A21\u62DFtry\u4EE3\u7801\u5757\uFF0C\u5C31\u50CFpromise.catch\u6A21\u62DF\u7684\u662Fcatch\u4EE3\u7801\u5757\u3002</p></blockquote>`,40),l=[c],P={__name:"ECMAScript 6 \u5165\u95E8--Promise \u5BF9\u8C61",setup(s,{expose:e}){return e({frontmatter:{}}),(i,m)=>(o(),r("div",p,l))}};export{P as default};
